local Calculator = {                                                                                                            add = function(x, y)                                                                                                            return x + y                                                                                                    end,                                                                                                                    substract = function(x, y)                                                                                                      return x - y                                                                                                    end,                                                                                                                    multiply = function(x, y)                                                                                                       return x * y                                                                                                    end                                                                                                             }                                                                                                                                                                                                                                               print(assert(Calculator.add(10,20) == 30))                                                                              print(assert(Calculator.substract(10,20) == -10))                                                                       print(assert(Calculator.multiply(10,20) == 200))                                                                                                                                                                                                return Calculator
